\<template>
  <div class="app-container list">
    <div class="toolbar">
      <el-input v-model.trim="query.orderName" class="query-item" style="width: 140px" placeholder="所属订单名称" clearable @clear="handleQuery" />
      <el-input v-model.trim="query.orderCode" class="query-item" style="width: 140px" placeholder="所属订单编号" clearable @clear="handleQuery" />
      <el-input v-model.trim="query.goodsInspector" class="query-item" style="width: 140px" placeholder="检验人员" clearable @clear="handleQuery" />
      <el-button class="tool tool-query" type="primary" icon="el-icon-search" @click="handleQuery">查询</el-button>
    </div>

    <el-table ref="listTable" v-loading="loading.list" v-adaptive="{ bottomOffset: 55 }" height="200px" :data="datas" :default-sort="sort" border fit highlight-current-row @sort-change="handleSort">
      <!--      <el-table-column type="selection" align="center" width="35" />-->
      <el-table-column label="序号" type="index" align="center" width="65" fixed>
        <template slot-scope="scope">
          <span>{{ (page.current - 1) * page.size + scope.$index + 1 }}</span>
        </template>
      </el-table-column>
      <el-table-column label="订单编号" prop="order.code" :sort-orders="sortOrders" align="center" width="200" show-overflow-tooltip />
      <el-table-column label="所属订单" prop="order.name" :sort-orders="sortOrders" align="center" width="200" show-overflow-tooltip />
      <el-table-column label="数量" prop="goods.quality" :sort-orders="sortOrders" align="center" width="200" show-overflow-tooltip />
      <el-table-column label="检验时间" prop="goods.inspectionTime" :sort-orders="sortOrders" align="left" show-overflow-tooltip />
      <el-table-column label="检验人员" prop="goods.inspector" :sort-orders="sortOrders" align="left" show-overflow-tooltip />
      <el-table-column fixed="right" label="操作" align="center" width="180">
        <template slot-scope="{row}">
          <el-tooltip v-if="access.detail.allow" class="item" effect="dark" content="详情" placement="top-end">
            <el-button type="primary" plain class="button-operate button-detail" size="mini" @click="handleDetail(row)"><i class="vue-icon-detail" /></el-button>
          </el-tooltip>
          <el-tooltip v-if="access.update.allow" class="item" effect="dark" content="退回" placement="top-end">
            <el-button type="danger" plain class="button-operate button-update" size="mini" @click="handleReturn(row)"><i class="el-icon-box" /></el-button>
          </el-tooltip>
        </template>
      </el-table-column>
    </el-table>

    <pagination :hidden="page.total==0" :total="page.total" :page.sync="page.current" :limit.sync="page.size" @pagination="getDatas" />

    <el-dialog v-loading="loading.detail" custom-class="dialog-fullscreen dialog-detail" :title="detail.dialog.title" :visible.sync="detail.dialog.visible" :modal="false" :modal-append-to-body="false">
      <el-form ref="formDetail" label-position="right" :label-width="detail.dialog.labelWidth">
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="所属订单编号">
              {{ detail.models.order.code }}
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="所属订单名称">
              {{ detail.models.order.name }}
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="数量">
              {{ detail.models.goods.quality }}
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="检验时间">
              {{ detail.models.goods.inspectionTime }}
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="检验人员">
              {{ detail.models.goods.inspector }}
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
    </el-dialog>
    <el-dialog :title="goodsReturn.dialog.title" :visible.sync="goodsReturn.dialog.visible" :modal="false" :modal-append-to-body="false" :close-on-click-modal="false">
      <el-form ref="formCreate" :rules="goodsReturn.rules" :model="goodsReturn.models" label-position="right" :label-width="goodsReturn.dialog.labelWidth">
        <el-row>
          <el-col :xl="15">
            <el-form-item label="货物条码/RFID码" prop="goodsCode">
              <el-input v-model="goodsReturn.models.goodsCode" />
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="15">
            <el-form-item label="原存放地条码/RFID码" prop="locationCode">
              <el-input v-model="goodsReturn.models.locationCode" />
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
      <div slot="footer" class="dialog-footer">
        <el-button @click="goodsReturn.dialog.visible = false">取消</el-button>
        <el-button type="primary" @click="confirmReturn()">确认退回</el-button>
      </div>
    </el-dialog>

  </div>
</template>

<script>
import adaptive from '@/directive/el-table'
import setRule from '@/utils/form-validate'
import Pagination from '@/components/Pagination'
import * as goodsReturn from '@/api/goods-manage/goods-return'

export default {
  name: 'GoodsReturn',
  components: { Pagination },
  directives: { adaptive },
  data() {
    return {
      access: this.$store.getters.access['GoodsManage']['GoodsColour'],
      datas: null,
      fileList: [],
      query: { orderCode: null, orderName: null, goodsInspector: null },
      page: { total: 0, current: 1, size: 20 },
      sort: { prop: 'sort', order: 'ascending' },
      detail: {
        dialog: { title: '退货信息', visible: false, labelWidth: '120px' },
        models: { order: { code: null, name: null }, goods: { quality: null, inspectionTime: null, inspector: null }}
      },
      goodsReturn: {
        dialog: { title: '退回货物', visible: false, labelWidth: '150px' },
        models: { goodsCode: null, locationCode: null },
        rules: {
          goodsCode: setRule('货物条码/RFID码', [{ required: true }, { length: [0, 20] }]),
          locationCode: setRule('原存放地条码/RFID码', [{ required: true }, { length: [0, 20] }])
        }
      },
      upload: {
        dialog: { title: '导入文件', visible: false, labelWidth: '150px' }
      },
      loading: { list: true, export: false, detail: false, update: false, upload: false },
      sortOrders: ['ascending', 'descending']
    }
  },
  created() {
    this.getDatas()
  },
  methods: {
    getDatas() {
      this.loading.list = true
      goodsReturn.getList(this.query, this.page, this.sort).then(response => {
        this.datas = response.data.item
        this.page.total = response.data.total
        this.loading.list = false
      }).catch(reject => {
        this.loading.list = false
      })
    },
    handleQuery() {
      this.page.current = 1
      this.getDatas()
    },
    handleSort(data) {
      this.sort.prop = data.prop
      this.sort.order = data.order
      this.getDatas()
    },
    handleDetail(row) {
      // 若列表数据展示了全部属性，则可直接拷贝列表数据
      this.detail.models = Object.assign({}, row)
      this.detail.dialog.visible = true
      this.loading.detail = true
      console.log(this.detail.models)
      // api.get(row.id).then(response => {
      //   this.detail.models = response.data
      //   this.loading.detail = false
      // }).catch(reject => {
      //   this.loading.detail = false
      // })
      this.loading.detail = false
    },
    handleReturn(row) {
      this.goodsReturn.models.locationCode = row.goods.locationBarCode !== null ? row.goods.locationBarCode : row.goods.locationRfidCode
      this.goodsReturn.models.goodsCode = row.goods.goodsBarCode !== null ? row.goods.goodsBarCode : row.goods.goodsRfidCode
      goodsReturn.returnGoods(this.goodsReturn.models).then(response => {
        this.goodsReturn.models.goodsCode = null
        this.goodsReturn.models.locationCode = null
        this.getDatas()
        this.loading.list = false
      }).catch(reject => {
        this.loading.list = false
      })
    },
    confirmReturn() {
      goodsReturn.returnGoods(this.goodsReturn.models).then(response => {
        this.goodsReturn.models.goodsCode = null
        this.goodsReturn.models.locationCode = null
        this.loading.list = false
      }).catch(reject => {
        this.loading.list = false
      })
    },
    handleDelete(row) {
      this.deleteData([row.id])
    },
    handleDeletes(row) {
      if (this.$refs.listTable.selection.length === 0) {
        this.$message({ type: 'warning', message: '请在下面表格的左侧勾选需要删除的数据', duration: 4000 })
        return false
      }
      const ids = []
      for (const item of this.$refs.listTable.selection) {
        ids.push(item.id)
      }
      this.deleteData(ids)
    },
    deleteData(ids) {
      this.$confirm('数据删除后，不可恢复, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        goodsReturn.del(ids).then(response => {
          // 重新获取数据
          this.getDatas()
        }).catch(reject => {
        })
      })
    }
  }
}
</script>

<style lang="scss" scoped>
</style>
