\<template>
  <div class="app-container list">
    <div class="toolbar">
      <el-input v-model.trim="query.code" class="query-item" style="width: 120px" placeholder="颜色编码" clearable @clear="handleQuery" />
      <el-input v-model.trim="query.name" class="query-item" style="width: 120px" placeholder="颜色名称" clearable @clear="handleQuery" />
      <el-button class="tool tool-query" type="primary" icon="el-icon-search" @click="handleQuery">查询</el-button>
      <el-button v-if="access.create.allow" class="tool tool-create" type="primary" icon="vue-icon-create" @click="handleCreate">添加</el-button>
      <el-button v-if="access.delete.allow" class="tool tool-delete" type="danger" icon="vue-icon-delete" @click="handleDeletes">批量删除</el-button>
      <el-button v-if="access.import.allow" class="tool tool-import" :loading="loading.upload" type="primary" icon="vue-icon-import" @click="handleUpload">导入</el-button>
      <el-button v-if="access.export.allow" class="tool tool-export" :loading="loading.export" type="primary" icon="vue-icon-excel" @click="handleExport">导出</el-button>
    </div>

    <el-table ref="listTable" v-loading="loading.list" v-adaptive="{ bottomOffset: 55 }" height="200px" :data="datas" :default-sort="sort" border fit highlight-current-row @sort-change="handleSort">
      <el-table-column type="selection" align="center" width="35" />
      <el-table-column label="序号" type="index" align="center" width="65" fixed>
        <template slot-scope="scope">
          <span>{{ (page.current - 1) * page.size + scope.$index + 1 }}</span>
        </template>
      </el-table-column>
      <el-table-column label="编号" prop="code" :sort-orders="sortOrders" align="center" width="200" show-overflow-tooltip />
      <el-table-column label="名称" prop="name" :sort-orders="sortOrders" align="center" width="200" show-overflow-tooltip />
      <el-table-column label="描述" prop="description" :sort-orders="sortOrders" align="center" show-overflow-tooltip />
      <el-table-column fixed="right" label="操作" align="center" width="180">
        <template slot-scope="{row}">
          <el-tooltip v-if="access.update.allow" class="item" effect="dark" content="排序上移" placement="top-end">
            <el-button type="primary" plain class="button-operate button-rankup" size="mini" @click="handleRankup(row)"><i class="vue-icon-rankup" /></el-button>
          </el-tooltip>
          <el-tooltip v-if="access.detail.allow" class="item" effect="dark" content="详情" placement="top-end">
            <el-button type="primary" plain class="button-operate button-detail" size="mini" @click="handleDetail(row)"><i class="vue-icon-detail" /></el-button>
          </el-tooltip>
          <el-tooltip v-if="access.update.allow" class="item" effect="dark" content="编辑" placement="top-end">
            <el-button type="primary" plain class="button-operate button-update" size="mini" @click="handleUpdate(row)"><i class="vue-icon-update" /></el-button>
          </el-tooltip>
          <el-tooltip v-if="access.delete.allow" class="item" effect="dark" content="删除" placement="top-end">
            <el-button type="danger" plain class="button-operate button-delete" size="mini" @click="handleDelete(row)"><i class="vue-icon-delete" /></el-button>
          </el-tooltip>
        </template>
      </el-table-column>
    </el-table>

    <pagination :hidden="page.total==0" :total="page.total" :page.sync="page.current" :limit.sync="page.size" @pagination="getDatas" />

    <el-dialog v-loading="loading.upload" custom-class="dialog-upload" :title="upload.dialog.title" :visible.sync="upload.dialog.visible" :modal="false" :modal-append-to-body="false">
      <el-row>
        <el-col :xl="6" :md="12" :sm="24">
          <el-button class="tool tool-create" type="primary" icon="vue-icon-excel" @click="ExportTemplate">点击导出数据模板</el-button>
        </el-col>
      </el-row>
      <br>
      <el-row>
        <el-col :xl="6" :md="12" :sm="24">
          <el-upload
            ref="upload"
            class="upload-demo"
            action=""
            :on-remove="handleRemove"
            :http-request="uploadUrl"
            :file-list="fileList"
            accept=".xlsx, .xls"
            :auto-upload="false"
          >
            <el-button slot="trigger" size="small" type="primary">选取文件</el-button>
            <el-button style="margin-left: 10px;" size="small" type="success" @click="submitUpload">上传到服务器</el-button>
            <div slot="tip" class="el-upload__tip">仅支持上传Excel文件</div>
          </el-upload>
        </el-col>
      </el-row>
    </el-dialog>

    <el-dialog v-loading="loading.detail" custom-class="dialog-detail" :title="detail.dialog.title" :visible.sync="detail.dialog.visible" :modal="false" :modal-append-to-body="false">
      <el-form ref="formDetail" label-position="right" :label-width="detail.dialog.labelWidth">
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="颜色编号">
              {{ detail.models.code }}
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="颜色名称">
              {{ detail.models.name }}
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="描述">
              {{ detail.models.description }}
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
    </el-dialog>

    <el-dialog custom-class="dialog-create" :title="create.dialog.title" :visible.sync="create.dialog.visible" :modal="false" :modal-append-to-body="false" :close-on-click-modal="false">
      <el-form ref="formCreate" :rules="create.rules" :model="create.models" label-position="right" :label-width="create.dialog.labelWidth">
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="名颜编码" prop="code">
              <el-input v-model="create.models.code" />
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="名颜色称" prop="name">
              <el-input v-model="create.models.name" />
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="描述" prop="description">
              <el-input v-model="create.models.description" />
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
      <div slot="footer" class="dialog-footer">
        <el-button @click="create.dialog.visible = false">取消</el-button>
        <el-button type="primary" @click="createData()">提交</el-button>
      </div>
    </el-dialog>

    <el-dialog v-loading="loading.update" custom-class="dialog-update" :title="update.dialog.title" :visible.sync="update.dialog.visible" :modal="false" :modal-append-to-body="false" :close-on-click-modal="false">
      <el-form ref="formUpdate" :rules="update.rules" :model="update.models" label-position="right" :label-width="update.dialog.labelWidth">
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="颜色编码" prop="code">
              <el-input v-model="update.models.code" />
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="名称" prop="name">
              <el-input v-model="update.models.name" />
            </el-form-item>
          </el-col>
        </el-row>
        <el-row>
          <el-col :xl="6" :md="12" :sm="24">
            <el-form-item label="描述" prop="description">
              <el-input v-model="update.models.description" />
            </el-form-item>
          </el-col>
        </el-row>
      </el-form>
      <div slot="footer" class="dialog-footer">
        <el-button @click="update.dialog.visible = false">取消</el-button>
        <el-button type="primary" @click="updateData()">提交</el-button>
      </div>
    </el-dialog>
  </div>
</template>

<script>
import adaptive from '@/directive/el-table'
import setRule from '@/utils/form-validate'
import Pagination from '@/components/Pagination'
import * as goodsColor from '@/api/goods-manage/goods-color'

export default {
  name: 'GoodsColour',
  components: { Pagination },
  directives: { adaptive },
  data() {
    return {
      access: this.$store.getters.access['GoodsManage']['GoodsColour'],
      datas: null,
      fileList: [],
      query: { code: null, name: null },
      page: { total: 0, current: 1, size: 20 },
      sort: { prop: 'sort', order: 'ascending' },
      detail: {
        dialog: { title: '货物颜色信息', visible: false, labelWidth: '120px' },
        models: { code: null, name: null, description: null }
      },
      create: {
        dialog: { title: '添加货物颜色', visible: false, labelWidth: '120px' },
        models: { code: null, name: null, description: null },
        rules: {
          code: setRule('货物颜色编码', [{ required: true }, { length: [0, 20] }]),
          name: setRule('货物颜色名称', [{ required: true }, { length: [0, 20] }]),
          description: setRule('货物颜色描述', [{ length: [0, 50] }])
        }
      },
      update: {
        dialog: { title: '编辑货物颜色信息', visible: false, labelWidth: '120px' },
        models: { id: null, code: null, name: null, description: null },
        rules: {
          code: setRule('货物颜色编码', [{ required: true }, { length: [0, 20] }]),
          name: setRule('货物颜色名称', [{ required: true }, { length: [0, 20] }]),
          description: setRule('货物颜色描述', [{ length: [0, 50] }])
        }
      },
      upload: {
        dialog: { title: '导入文件', visible: false, labelWidth: '150px' }
      },
      loading: { list: true, export: false, detail: false, update: false, upload: false },
      sortOrders: ['ascending', 'descending']
    }
  },
  created() {
    this.getDatas()
  },
  methods: {
    getDatas() {
      this.loading.list = true
      goodsColor.getList(this.query, this.page, this.sort).then(response => {
        this.datas = response.data.items
        this.page.total = response.data.total
        this.loading.list = false
      }).catch(reject => {
        this.loading.list = false
      })
    },
    handleQuery() {
      this.page.current = 1
      this.getDatas()
    },
    handleSort(data) {
      this.sort.prop = data.prop
      this.sort.order = data.order
      this.getDatas()
    },
    handleDetail(row) {
      // 若列表数据展示了全部属性，则可直接拷贝列表数据
      this.detail.models = Object.assign({}, row)
      this.detail.dialog.visible = true
      this.loading.detail = true
      // api.get(row.id).then(response => {
      //   this.detail.models = response.data
      //   this.loading.detail = false
      // }).catch(reject => {
      //   this.loading.detail = false
      // })
      this.loading.detail = false
    },
    handleCreate() {
      this.create.dialog.visible = true
    },
    createData() {
      this.$refs['formCreate'].validate((valid) => {
        if (!valid) return false
        goodsColor.create(this.create.models).then(response => {
          // 为方便连续添加，create对话框不关闭
          // 需要清空的表单，手动清空，至少清空一个必填项，防止点击两遍
          this.create.models.code = ''
          this.create.models.name = ''
          // 重新获取数据
          this.getDatas()
        }).catch(reject => {
        })
      })
    },
    handleRankup(row) {
      this.loading.list = true
      goodsColor.rankup(row.id).then(response => {
        this.getDatas()
        this.loading.list = false
      }).catch(reject => {
        this.loading.list = false
      })
    },
    handleUpdate(row) {
      //  若列表数据展示了全部属性，则可直接拷贝列表数据用于编辑
      this.update.models = Object.assign({}, row)
      this.update.dialog.visible = true
      this.loading.update = true
      // goodsType.get(row.id).then(response => {
      //   this.update.models = response.data
      //   this.loading.update = false
      // }).catch(reject => {
      //   this.loading.update = false
      // })
      this.$nextTick(() => {
        this.$refs['formUpdate'].clearValidate()
      })
      this.loading.update = false
    },
    updateData() {
      this.$refs['formUpdate'].validate((valid) => {
        if (!valid) return false
        const tempData = Object.assign({}, this.update.models)
        goodsColor.update(tempData).then(response => {
          // 重新获取数据
          this.getDatas()
          this.update.dialog.visible = false
        }).catch(reject => {
        })
      })
    },
    handleDelete(row) {
      this.deleteData([row.id])
    },
    handleDeletes(row) {
      if (this.$refs.listTable.selection.length === 0) {
        this.$message({ type: 'warning', message: '请在下面表格的左侧勾选需要删除的数据', duration: 4000 })
        return false
      }
      const ids = []
      for (const item of this.$refs.listTable.selection) {
        ids.push(item.id)
      }
      this.deleteData(ids)
    },
    deleteData(ids) {
      this.$confirm('数据删除后，不可恢复, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        goodsColor.del(ids).then(response => {
          // 重新获取数据
          this.getDatas()
        }).catch(reject => {
        })
      })
    },
    handleUpload() {
      this.upload.dialog.visible = true
    },
    submitUpload() {
      this.$refs.upload.submit()
    },
    handleRemove() {
      this.$refs.upload.clearFiles()
    },
    uploadUrl(param) {
      // 先判断图片大小
      const _file = param.file
      // const _fileSize = _file.size < 1024 * 500
      // if (!_fileSize) {
      //   that.$message.warning('请上传500KB以下的文件')
      //   return false
      // }
      // 参数
      const params = new FormData()
      params.append('excel', _file)
      goodsColor.uploadExcel(params).then(response => {
        const failureDetails = response.data.failureDetails
        let message = ''
        failureDetails.forEach((item) => {
          message = message + '/' + item
        })
        this.$alert('成功：' + response.data.success + ' ' +
          '失败：' + response.data.failure + ' ' +
          '读取总数：' + response.data.total + ' ' +
          ' \n ' + message,
        '消息提示',
        {
          confirmButtonText: '确定',
          callback: action => {
            this.upload.dialog.visible = false
            this.handleRemove()
            this.getDatas()
          }
        })
      }).catch(reject => {
      })
    },
    ExportTemplate() {
      this.loading.export = true
      import('@/vendor/Export2Excel').then(excel => {
        const tHeader = ['编号', '名称', '描述']
        const data = ''
        excel.export_json_to_excel({
          header: tHeader,
          data,
          filename: '货物颜色'
        })
        this.loading.export = false
      })
    },
    handleExport() {
      this.loading.export = true
      goodsColor.exportExcel(this.query, this.page, this.sort).then(response => {
        const blob = new Blob([response])
        const fileName = '货物颜色.xls'
        const elink = document.createElement('a')
        elink.onload = () => { URL.revokeObjectURL(elink.href) }
        elink.download = fileName
        elink.style.display = 'none'
        elink.href = URL.createObjectURL(blob)
        document.body.appendChild(elink)
        elink.click()
        document.body.removeChild(elink)
        this.loading.export = false
      }).catch(reject => {
        this.loading.export = false
      })
    }
  }
}
</script>

<style lang="scss" scoped>
</style>
